var bit_stream = msg.payload;
var data = {};

// reorder and standardize checksum string
const pid_index = bit_stream.indexOf("PID");
bit_stream = bit_stream.slice(pid_index-2, bit_stream.indexOf("Checksum", pid_index+1)+10);

//calculate checksum for data integrity
var checksum = 0;
for(const element of bit_stream) {
   checksum = (checksum + element) & 255; /* Take modulo 256 in account */
}

// if data is not corrupted
if(checksum == 0){

    // convert bitstream to string
    var string = bit_stream.toString();
    // remove checksum, lf and cr
    string = string.substring(2, string.indexOf("Checksum")-1);
    // convert string to array
    var array = string.split('\n');
    // convert array of string into data object
    for(const element of array){
        var property_name = element.substring(0, element.indexOf('\t'));
        var property_value = element.substring(element.indexOf('\t')+1, element.length-1);
        data[property_name] = property_value;
    }

    // check data object for integrity before saving it
    if(Object.keys(data).length == 19){
        // update flow object with new data
        global.set("mppt_broadcast", data);

