const data = msg.payload;
const max_capacity = 384; //Wh (12.5V * 30Ah)
const energy_yield = data.energy_yield*10; //Wh
const energy_consumed = data.energy_consumed*10;   //Wh
var offset_energy = context.get("offset_energy") || max_capacity;
var today_number = flow.get("today_number") || data.day_sequence_number;

// if its a new day
if(today_number+1 == data.day_sequence_number){
    today_number = data.day_sequence_number;
    context.set("offset_energy", (context.get("tomorrow_offset_energy") || offset_energy));
}

// if battery fully charged (float mode)
if(flow.get("mppt_broadcast").CS == '5'){
    context.set("offset_energy", (max_capacity - energy_yield + energy_consumed));
}

var left_energy = offset_energy + energy_yield - energy_consumed;

// adjust offset if error detected (SOC > 100%)
if(left_energy > max_capacity){
    offset_energy = max_capacity - energy_yield + energy_consumed;
    context.set("offset_energy", offset_energy);
    left_energy = offset_energy + energy_yield - energy_consumed;
}
// SOC < 0%
else if(left_energy < 0){
    context.set("offset_energy", (0 - energy_yield + energy_consumed));
    left_energy = 0;
}

context.set("tomorrow_offset_energy", left_energy);

var soc = left_energy * 100 / max_capacity;
msg.payload = soc;
return msg;

var raw_data = msg.payload;
var data = {
    energy_yield:           [8, 16],    //*10 to Wh
    energy_consumed:        [16, 24],   //*10 to Wh
    battery_voltage_max:    [24, 28],   //*10 to mV
    battery_voltage_min:    [28, 32],   //*10 to mV
    error_database:         [32, 34],
    error_0:                [34, 36],
    error_1:                [36, 38],
    error_2:                [38, 40],
    error_3:                [40, 42],
    time_bulk:              [42, 46],   // minutes
    time_absorpion:         [46, 50],   // minutes
    time_float:             [50, 54],   // minutes
    power_max:              [54, 62],   // in W
    battery_current_max:    [62, 66],   //*100 in mA
    panel_voltage_max:      [66, 70],   //*10 in mV
    day_sequence_number:    [70, 74]
};
// decoding routine
for(const property in data){
    // save substring of interest in array of bytes
    var byte_arr = raw_data.substring(data[property][0], data[property][1]).match(/.{1,2}/g);
    // invert low nibble with high nibble
    var inverted_arr = [];
    for(var i=byte_arr.length-1; i>=0; i--){
        inverted_arr.push(byte_arr[i]);
    }
    // join array of bytes into string then parse string into dec value
    data[property] = parseInt(inverted_arr.join(''), 16);
}


flow.set("today_history", data);
msg.payload = data;
return msg;

var string = msg.payload;
var data = {};

// reorder and standardize data string
string = string.substring(string.indexOf("PID"), string.indexOf("PID", string.indexOf("PID")+1));
//calculate checksum for data integrity
var checksum = 0;
var bit_stream = new Buffer(string);
for(const element of bit_stream) {
   checksum = (checksum + element) & 255; /* Take modulo 256 in account */
}
if(checksum == 0){
    // remove checksum
    string = string.substring(0, string.indexOf("Checksum")-1);
    // convert string to array
    var array = string.split('\n');
    // convert array of string into data object
    for(const element of array){
        var property_name = element.substring(0, element.indexOf('\t'));
        var property_value = element.substring(element.indexOf('\t')+1, element.length-1);
        data[property_name] = property_value;
    }
    // update flow object with new data
    flow.set("mppt_broadcast", data);
    return [{payload: data} ,null];
}
else {
    // data corrucped flag
    return [null, {payload: true}];
}

