var algorithm=flow.get('algorithm') || 0;

if(algorithm == "battery life"){

    var lfp_soc = global.get('lfp_soc') || 0;
    var fridge_temp = flow.get('fridge_temp') || 0;

    const UPPER_FRIDGE_TEMP_THRESHOLD = 10;
    const LOWER_FRIDGE_TEMP_THRESHOLD = 6;
    const UPPER_LFP_SOC_THRESHOLD = 35;
    const LOWER_LFP_SOC_THRESHOLD = 20;

    if(lfp_soc >= UPPER_LFP_SOC_THRESHOLD && fridge_temp >= UPPER_FRIDGE_TEMP_THRESHOLD) {
        msg.payload = true;
        return msg;
    }
    else if(lfp_soc < LOWER_LFP_SOC_THRESHOLD) {
        flow.set('low_soc', true);
        msg.payload = false;
        return msg;
    }
    else if(fridge_temp < LOWER_FRIDGE_TEMP_THRESHOLD) {
        msg.payload = false;
        return msg;
    }
    else {
        msg.payload = Boolean(global.get('fridge_status') || 0);
        return msg;
    }
}

